class ScoringPrimitives:
    """IDF, TF, saturation, length norm, aggregation. Invent new primitives or new formulas."""

    @staticmethod
    def tf_salience(tf: float, dl: float, avgdl: float) -> float:
        """Length-aware TF salience: downweights inflated TF in long docs."""
        denom = tf + 0.5 * (dl / max(avgdl, 1.0)) + 1.0
        return tf / (denom + EvolvedParameters.epsilon)

    @staticmethod
    def tf_salience_vectorized(
        tf: NDArray[np.float64], dl: NDArray[np.float64], avgdl: float
    ) -> NDArray[np.float64]:
        denom = tf + 0.5 * (dl / max(avgdl, 1.0)) + 1.0
        return tf / (denom + EvolvedParameters.epsilon)

    @staticmethod
    def informativeness_gate(idf_bal: float, pivot: float, strength: float, floor: float) -> float:
        """
        New primitive: bounded informativeness multiplier in [floor, floor+strength].
        Uses balanced-idf (less spiky than lucene idf) and tanh compression.
        - Very common terms => ~floor
        - Moderately informative => rises smoothly
        - Ultra-rare => saturates (prevents domination)
        """
        f = max(0.0, min(1.0, floor))
        s = max(0.0, strength)
        x = (idf_bal - pivot) / (pivot + EvolvedParameters.epsilon)
        return f + s * (0.5 + 0.5 * math.tanh(x))

    @staticmethod
    def informativeness_gate_vectorized(
        idf_bal: NDArray[np.float64], pivot: float, strength: float, floor: float
    ) -> NDArray[np.float64]:
        f = float(max(0.0, min(1.0, floor)))
        s = float(max(0.0, strength))
        x = (idf_bal - pivot) / (pivot + EvolvedParameters.epsilon)
        return f + s * (0.5 + 0.5 * np.tanh(x))

    @staticmethod
    def idf_balanced(df: float, N: int) -> float:
        """
        Bounded rarity for coordination/priors: log1p((1-p)/p) where p=df/N.
        Helps avoid ultra-rare terms fully dominating rarity-mass coverage.
