class TermScorer:
    """One term's contribution. Evolve the formula; invent new combinations or new math."""

    @staticmethod
    def score(tf: float, df: float, N: int, dl: float, avgdl: float) -> float:
        if tf <= 0:
            return 0.0
        k1, b = EvolvedParameters.k1, EvolvedParameters.b

        idf = ScoringPrimitives.idf_lucene(df, N)
        idf = max(EvolvedParameters.min_idf, min(idf, EvolvedParameters.max_idf))

        # Use balanced idf only as a *gate driver* (bounded), not as the main magnitude.
        idf_bal = ScoringPrimitives.idf_balanced(df, N)
        info = ScoringPrimitives.informativeness_gate(
            idf_bal,
            EvolvedParameters.info_pivot,
            EvolvedParameters.info_strength,
            EvolvedParameters.info_floor,
        )

        norm = ScoringPrimitives.length_norm_bm25(dl, avgdl, b)

        tf_sat = ScoringPrimitives.saturate_lucene(tf, k1, norm)
        tf_sal = ScoringPrimitives.tf_salience(tf, dl, avgdl)
        a = float(max(0.0, min(1.0, EvolvedParameters.tf_salience_alpha)))
        tf_comp = (1.0 - a) * tf_sat + a * tf_sal

        rescue = ScoringPrimitives.commonness_rescue(
            idf, EvolvedParameters.common_strength, EvolvedParameters.common_pivot
        )
        return idf * tf_comp * rescue * info


