def idf(df: NDArray[np.float64], N: int) -> NDArray[np.float64]:
    """EVOLVE: Robertson BM25 IDF log((N-df+0.5)/(df+0.5)); alternatives: Lucene log(1+(N-df+0.5)/(df+0.5)), ATIRE log(N/df), BM25L log((N+1)/(df+0.5)), BM25+ log((N+1)/df)."""
