        term_ids = []
        w_arr = []
        for t, w in zip(terms, weights):
            tid = self.corpus.get_term_id(t)
            if tid is not None:
                term_ids.append(tid)
                w_arr.append(w)
        if not term_ids:
            return np.arange(self.corpus.N, dtype=np.int64), np.zeros(self.corpus.N, dtype=np.float64)
