# -----------------------------------------------------------------------------

def idf(df: NDArray[np.float64], N: int) -> NDArray[np.float64]:
    """EVOLVE: Robertson IDF log((N-df+0.5)/(df+0.5))."""
    return np.log((N - df + 0.5) / (df + 0.5) + 1e-9)


# -----------------------------------------------------------------------------
