            return np.array([], dtype=np.float64)
        norms = self.norm_array[candidate_docs]
        scores = np.zeros(len(candidate_docs), dtype=np.float64)
        for i, tid in enumerate(query_term_ids):
            idf_val = self.idf_array[tid]
            if idf_val <= 0:
