        self._df = np.zeros(V, dtype=np.float64)

        for doc_idx, doc in enumerate(documents):
            seen = set()
            for term, count in Counter(doc).items():
                tid = self._vocab[term]
                tf_lil[tid, doc_idx] = count
                if tid not in seen:
                    self._posting_lists[tid].append(doc_idx)
                    self._df[tid] += 1
                    seen.add(tid)

