    if not query:
        return [], np.array([], dtype=np.float64)
    cnt = Counter(query)
    terms = list(cnt.keys())
    if mode == "unique":
        w = np.ones(len(terms), dtype=np.float64)
    elif mode == "count":
        w = np.array([float(cnt[t]) for t in terms], dtype=np.float64)
    elif mode == "saturated":
        qtf = np.array([float(cnt[t]) for t in terms], dtype=np.float64)
        w = (k3 + 1.0) * qtf / (k3 + qtf)
    else:
        w = np.array([float(cnt[t]) for t in terms], dtype=np.float64)
