    from numpy.typing import NDArray

NUM_QUERY_WORKERS = min(int(os.environ.get("BM25_QUERY_WORKERS", 32)), 32)
MIN_QUERIES_FOR_PARALLEL = 10


# -----------------------------------------------------------------------------
